#!/usr/bin/env node

import fs from'fs'
import { compileSync } from "@mdx-js/mdx"
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

console.log(
  `// DO NOT EDIT
// This file was generated by scripts/build-litepaper.js
`
  + compileSync(
    fs.readFileSync(
      `${__dirname}/../src/Litepaper/litepaper.mdx`,
      "utf8",
    ),
  ),
)
